package wy.study.learing.arithmetic;

public class Merge_sort {
    /**
     * 归并排序
     * 思想：分治法
     * 举例：8,7,2,3,1,6,9,-2,10
     * 第一次：{7.8}，{2，3}，{1,6}，{-2，9},{10}
     * 第二次：{2,3,7,8},{-2,1,6,9},{10}
     * 第三次：{-2,1,2,3,6,7,8,9},{10}
     * 第四次：{-2,1,2,3,6,7,8,9,10}
     */
    public static int[] MergeSort(int[] arrays) {
        int n = arrays.length % 2 == 0 ? (arrays.length / 2) : (arrays.length / 2 + 1);
        int index = 2;
        int start = 1;
        while (n > 1) {
            for (int i = 0; i < n; i++) {//分组
                for (int j = start; j < index; j++) {//遍历每个小组
                    if (arrays[j] > arrays[j - 1]) {
                        Swap.Swap(arrays, j, j + 1);
                    }
                }
                //start =
            }
            index = index * 2 > arrays.length ? arrays.length : index * 2;
            n = n % 2 == 0 ? (n / 2) : (n / 2 + 1);
        }

        return arrays;
    }

    public static void main(String[] args) {
        int[] arrays = new int[]{8, 7, 2, 3, 1, 6, 9, -2, 10};
        MergeSort(arrays);
        for (int i = 0; i < arrays.length; i++) {
            System.out.println(arrays[i]);
        }
    }
}
